import {
  USER_LOGIN_FAIL,
  USER_LOGIN_REQUEST,
  USER_LOGIN_SUCCESS,
  USER_LOGOUT
} from '../constants/userConstants'


export const userLogReducer = (state = {}, action) => {
    switch (action.type) {
        case USER_LOG_REQUEST:
          return {loading: true}
        case USER_LOG_SUCCESS:
          return {loading: false, userInfo: action.payload}
        case _LOG_FAIL:
          return {loading: false, error: action.payload}
        case USER_LOGOUT:
          return {}
        default: 
            return state
    }
}
